const menuBtn=document.querySelector(".navbar__menu-buttons"),menu=document.querySelector(".menu");toggleMenuBtn=()=>{for(var e of menuBtn.children)e.classList.toggle("active")},menuBtn.addEventListener("click",function(){toggleMenuBtn(),menu.classList.toggle("active")}),document.addEventListener("click",function(e){menu.contains(e.target)||menuBtn.contains(e.target)||(menu.classList.contains("active")&&toggleMenuBtn(),menu.classList.remove("active"))});const menuItem=document.querySelectorAll(".menu-item");menuItem.forEach(e=>{e.addEventListener("click",()=>{document.querySelector(".menu-item.active").classList.remove("active"),e.classList.add("active")})});